mapper.select:
SELECT i.store_id, f.title, COUNT(DISTINCT i.inventory_id) AS totalInventory ,COUNT(CASE WHEN (r.return_date IS NULL AND r.inventory_id IS NOT NULL) THEN 1 END) AS rental ,(COUNT(DISTINCT i.inventory_id) - COUNT(CASE WHEN (r.return_date IS NULL AND r.inventory_id IS NOT NULL) THEN 1 END)) AS stock FROM film f INNER JOIN inventory i ON f.film_id = i.film_id LEFT JOIN rental r ON i.inventory_id = r.inventory_id
f.title LIKE CONCAT('%',#{searchWord},'%')
AND i.store_id = #{storeId}
GROUP BY i.film_id, i.store_id LIMIT #{beginRow}, #{rowPerPage}

SELECT count(DISTINCT(i.film_id)) FROM film f, inventory i WHERE f.film_id = i.film_id
AND f.title LIKE CONCAT('%',#{searchWord},'%')
AND i.store_id = #{storeId}
