mapper.select:
SELECT rental_id rentalId, rental_date rentalDate, inventory_id inventoryId, customer_id customerId, return_date returnDate, staff_id staffId, last_update lastUpdate FROM rental WHERE return_date IS NULL
AND customer_id LIKE #{searchNum}
LIMIT #{beginRow},#{rowPerPage}

mapper.update:
UPDATE rental
   SET return_date = NOW() 
 WHERE rental_id = #{rentalId}

UPDATE payment 
   SET amount = amount + #{overdueFee} 
 WHERE rental_id = #{rentalId}
