mapper.select:
SELECT s.store_id stordId ,CONCAT(c.city, _utf8mb4',', cy.country) AS store ,CONCAT(m.first_name, _utf8mb4' ', m.last_name) AS manager ,SUM(p.amount) AS total_sales, YEAR(p.payment_date) AS YEAR, MONTH(p.payment_date) AS MONTH FROM payment AS p INNER JOIN rental AS r ON p.rental_id = r.rental_id INNER JOIN inventory AS i ON r.inventory_id = i.inventory_id INNER JOIN store AS s ON i.store_id = s.store_id INNER JOIN address AS a ON s.address_id = a.address_id INNER JOIN city AS c ON a.city_id = c.city_id INNER JOIN country AS cy ON c.country_id = cy.country_id INNER JOIN staff AS m ON s.manager_staff_id = m.staff_id
WHERE s.store_id = #{storeId}
GROUP BY s.store_id, MONTH(p.payment_date) ORDER BY p.payment_date
